"use strict";

var slider = document.querySelector('.slider');
var slides = slider.querySelectorAll('li');
var count = 0;
var currentClass = '';
var imgClass = ['top-left', 'top-right', 'bottom-left', 'bottom-right'];
step();

window.onkeydown = function (e) {
  if (e.keyCode == 39) {
    nextSlider();
  }

  if (e.keyCode == 37) {
    prewSlider();
  }
};

function prewSlider() {
  count--;

  if (count < 0) {
    count = slides.length - 1;
  }

  step();
}

function nextSlider() {
  count++;

  if (count >= slides.length) {
    count = 0;
  }

  step();
}

function step() {
  if (slider.querySelector('.active')) {
    slider.querySelector('.active').classList.remove('active');
  }

  if (currentClass) {
    removeRandomClass(slider.querySelector(".".concat(currentClass)));
  }

  slides[count].classList.add('active');
  slider.style.backgroundColor = randomRGB();

  if (slides[count].querySelector('img')) {
    randomSetClass(slides[count].querySelector('img'));
  }
}

function randomInteger(min, max) {
  // случайное число от min до (max+1)
  var rand = min + Math.random() * (max + 1 - min);
  return Math.floor(rand);
}

function randomRGB() {
  return "rgb(".concat(randomInteger(200, 220), ", ").concat(randomInteger(220, 255), ", ").concat(randomInteger(200, 220), ")");
}

function randomSetClass(elem) {
  currentClass = imgClass[randomInteger(0, imgClass.length - 1)];
  elem.classList.add(currentClass);
}

function removeRandomClass(elem) {
  setTimeout(function () {
    elem.classList.remove(currentClass);
  }, 200);
}
